sheet_contracts = pd.read_excel('contracts.xlsx', sheet_name='contracts', index_col='contract_address')
load_dotenv()

def getABI(address):
    try:
        abi = json.loads(sheet_contracts._get_value(address,'ABI'))
        return abi
    except:
        abi = requests.get('https://api.etherscan.io/api?module=contract&action=getabi&address=' + address + '&apikey=' + os.getenv('ETHERSCAN')).json()['result']
        return abi


