def getENS(web3, address):
    #web3 = Web3(Web3.HTTPProvider(os.getenv(env)))
    ns = ENS.fromWeb3(web3)
    address = web3.toChecksumAddress(address)

    address_ens = ns.name(address)

    if str(address_ens) == 'None':
        address_ens = address

    return address_ens

